from logging import getLogger
#start Ramani1
# from pymongo import MongoClient #comment
from azure.cosmos import CosmosClient #add
#end Ramani1

logger = getLogger(__name__)
clients = {}


def connect(db, **kwargs):
    try:
        return clients[db]
    except KeyError:
        logger.debug('New MongoClient connection')
        #start Ramani2
        # clients[db] = MongoClient(**kwargs, connect=False)    #comment
        clients[db] = CosmosClient(**kwargs)    #add
        #end Ramani2
    return clients[db]


class Error(Exception):  # NOQA: StandardError undefined on PY3
    pass


class InterfaceError(Error):
    pass


class DatabaseError(Error):
    pass


class DataError(DatabaseError):
    pass


class OperationalError(DatabaseError):
    pass


class IntegrityError(DatabaseError):
    pass


class InternalError(DatabaseError):
    pass


class ProgrammingError(DatabaseError):
    pass


class NotSupportedError(DatabaseError):
    pass


def Binary(value):
    return value
